// Declarações globais do p5.js para uso em modo global
declare function makeVector(x: number, y: number): any;
declare function createVector(x: number, y: number): any;
declare function createCanvas(width: number, height: number): any;
declare function background(color: number | string): void;
declare function fill(color: string | number, g?: number, b?: number, a?: number): void;
declare function rect(x: number, y: number, w: number, h: number): void;
declare function floor(n: number): number;
declare function abs(n: number): number;
declare function circle(x: number, y: number, d: number): void;
declare function loadImage(path: string): any;
declare function loadSound(path: string): any;
declare function image(img: any, x: number, y: number, w?: number, h?: number): void;
declare function imageMode(mode: any): void;
declare const CENTER: any;
declare const mouseX: number;
declare const mouseY: number;
